Thread t = new Thread(p);
t.start();
p.run(2);
}
}
class PrintWords implements Runnable {
int i = 0;
String str[] = {"Yes", "No"};
public void run() {
while ( i < 2 ) {
System.out.println(" Y or N " + str[i++]);
}
}

public void run(int j) {
while ( j >= 1 ) {
System.out.println(" Hello " + j-- );
}
}
}

